package run

import (
	"encoding/json"
	"log"

	"github.com/deepsourcelabs/cli/analysis/processor"
	"github.com/deepsourcelabs/cli/types"
)

// processAnalysisReport processes the analysis report generated by the Analyzer
func (a *AnalyzerDryRun) processAnalysisReport(reportBytes []byte) error {
	report := types.AnalysisReport{}
	// Convert the analysis result from the LSP based format to the default DeepSource format
	processor := processor.ProcessAnalysisResults{
		CodePath:   a.SourcePath,
		Processors: []string{"skip_cq", "source_code_load"},
	}

	if err := json.Unmarshal(reportBytes, &report); err != nil {
		log.Println(err)
		return err
	}
	processor.Report = report

	return processor.ProcessAnalysisResult()
}
